// JavaScript to scroll the container
const slider = document.getElementById("testimonial-slider");

// Scroll Right Function
const scrollRight = () => {
  slider.scrollBy({ left: 300, behavior: "smooth" });
};

// Scroll Left Function
const scrollLeft = () => {
  slider.scrollBy({ left: -300, behavior: "smooth" });
};

// Optional: Auto-scroll
setInterval(() => {
  scrollRight();
}, 5000);
